13 // Extended color enumeration, including a DEFAULT for resetting styles.
14 enum class Color { WHITE, BLACK, RED, GREEN, BLUE, CYAN, MAGENTA, YELLOW, LIGHT_GREEN, PINK, DEFAULT };
15 
16 class Style
17 {    
18 public:
19     // Convert a Color to its corresponding ANSI escape code.
20     static inline std::string Cta(Color color)
21     {
22         switch(color)
23         {
24             case Color::WHITE:       return "\033[37m";
25             case Color::BLACK:       return "\033[30m";
26             case Color::RED:         return "\033[31m";
27             case Color::GREEN:       return "\033[32m";
28             case Color::YELLOW:      return "\033[33m";
29             case Color::BLUE:        return "\033[34m";
30             case Color::MAGENTA:     return "\033[35m";
31             case Color::CYAN:        return "\033[36m";
32             case Color::LIGHT_GREEN: return "\033[92m"; // Bright green.
33             case Color::PINK:        return "\033[95m"; // Bright magenta.
34             case Color::DEFAULT:
35             default:                 return "\033[0m";
36         }
37     }
38 
39     // Convert a Color to its corresponding string.
40     static inline std::string Cts(Color color)
41     {
42         switch(color)
43         {
44             case Color::WHITE:       return "white";
45             case Color::BLACK:       return "black";
46             case Color::RED:         return "red";
47             case Color::GREEN:       return "green";
48             case Color::YELLOW:      return "yellow";
49             case Color::BLUE:        return "blue";
50             case Color::MAGENTA:     return "magenta";
51             case Color::CYAN:        return "cyan";
52             case Color::LIGHT_GREEN: return "light green";
53             case Color::PINK:        return "pink";
54             case Color::DEFAULT:
55             default:                 return "";
56         }
57     }
58 
59     static inline std::string BoldText(const std::string &text)
60     {
61         return "\033[1m" + text + "\033[0m";
62     }
63 
64     static inline std::string UnderlineText(const std::string &text)
65     {
66         return "\033[4m" + text + "\033[0m";
67     }
68 
69     static inline std::string LinkText(const std::string &text, Color continueColor = Color::DEFAULT)
70     {
71         return "\033[4m" + text + "\033[0m" + Cta(continueColor);
72     }
73 
74     // Reset the console color.
75     static inline std::string ResetColor()
76     {
77         return "\033[0m";
78     }
79 
80     // Helper function to calculate the visual length of a string,
81     // ignoring ANSI escape sequences.
82     static inline size_t DisplayLength(const std::string &text)
83     {
84         size_t length = 0;
85         bool in_escape = false;
86         for (size_t i = 0; i < text.size(); ++i)
87         {
88             if (!in_escape)
89             {
90                 if (text[i] == '\033')
91                     in_escape = true;
92                 else
93                     ++length;
94             }
95             else
96             {
97                 // Typically ANSI codes end with 'm'
98                 if (text[i] == 'm')
99                     in_escape = false;
100             }
101         }
102         return length;
103     }
104 
105     // Print text in a given color.
106     static inline void PrintColored(const std::string &text, Color color = Color::DEFAULT)
107     {
108         std::cout << Cta(color) << text << ResetColor();
109     }
110 
111     // Print text in a given color followed by a newline.
112     static inline void PrintColoredLine(const std::string &text, Color color = Color::DEFAULT, bool endl = true)
113     {
114         PrintColored(text, color);
115         if (endl)
116             std::cout << std::endl;
117     }
118 
119     // Print a horizontal line using a specified Unicode character.
120     static inline void PrintLine(std::string ch = "‚îÄ", int length = 50, Color color = Color::DEFAULT)
121     {
122         std::string line{};
123         for (int i = 0; i < length; i++)
124             line += ch;
125         
126         PrintColoredLine(line, color, false);
127     }
128 
129     // Print a styled title with a border made of Unicode box-drawing characters.
130     static inline void PrintTitle(const std::string &title, Color color = Color::CYAN)
131     {
132         int padding = 12;
133         int titleLength = DisplayLength(title);
134         int totalWidth = titleLength + padding;
135         // Title line.
136         std::cout << "\033[1m" << Cta(color)
137                     << '*' << std::string(totalWidth, '=') << ' ' 
138                     << title << ' ' << std::string(totalWidth, '=')
139                     << '*' << ResetColor() << std::endl;
140     }
141 
142     // Print a box around a given (possibly multi-line) text.
143     static inline void PrintBox(const std::string &text, const std::string &title = "", Color color = Color::GREEN, int fixOffset = 0)
144     {
145         std::istringstream iss(text);
146         std::string line;
147         int maxLength = 0;
148         std::vector<std::string> lines;
149         while (std::getline(iss, line))
150         {
151             lines.push_back(line);
152             int dlen = DisplayLength(line);
153             if (dlen > maxLength)
154                 maxLength = dlen;
155         }
156         // Top border.
157         std::cout << Cta(color) << "‚îå‚îÄ" << title;
158         PrintLine("‚îÄ", maxLength + 1 - static_cast<int>(DisplayLength(title)) + fixOffset, color);
159         std::cout << Cta(color) << "‚îê" << ResetColor() << std::endl;
160         // Content lines with side borders.
161         for (const auto &l : lines)
162         {
163             std::cout << Cta(color) << "‚îÇ " << ResetColor();
164             std::cout << l << std::string(maxLength - DisplayLength(l), ' ') 
165                         << Cta(color) << " ‚îÇ" << ResetColor() << std::endl;
166         }
167         // Bottom border.
168         std::cout << Cta(color) << "‚îî";
169         PrintLine("‚îÄ", maxLength + 2, color);
170         std::cout << Cta(color) << "‚îò" << ResetColor() << std::endl;
171     }
