204     // Clear the console screen (works on Unix-like systems with ANSI support).
205     static inline void ClearConsole()
206     {
207         #ifdef DEBUG
208             std::cout << std::endl;
209             return;
210         #endif
211         std::cout << "\033[2J\033[H";
212     }
