215     static inline bool GetBoolInput(const std::string &prompt, const std::string &title = "", Color color = Color::YELLOW, Color titleColor = Color::CYAN)
216     {
217         if (!title.empty())
218             PrintColoredLine(BoldText(title), titleColor);
219         
220         PrintColored(prompt + " [Y/n] ", color);
221         char in = std::cin.get();
222         // Flush any leftover characters (including newline)
223         std::cin.ignore(std::numeric_limits<std::streamsize>::max(), '\n');
224         std::cout << std::endl;
225         return in == 'Y' || in == 'y';
226     }
227 
228     static inline std::string GetStringInput(const std::string &prompt, const std::string &title = "", Color color = Color::YELLOW, Color titleColor = Color::CYAN)
229     {
230         if (!title.empty())
231             PrintColoredLine(BoldText(title), titleColor);
232         
233         PrintColored(prompt, color);
234         std::string input;
235         std::getline(std::cin, input);
236         return input;
237     }
