240     // Print a divider line with an optional centered message.
241     static inline void PrintDivider(const std::string &message = "", Color color = Color::DEFAULT)
242     {
243         int width = 50;
244         if (message.empty())
245         {
246             PrintLine("‚îÄ", width, color);
247             std::cout << std::endl;
248         }
249         else
250         {
251             int pad = (width - static_cast<int>(DisplayLength(message)) - 2) / 2;
252             std::cout << Cta(color) << std::string(pad, ' ') 
253                         << " " << message << " " 
254                         << std::string(pad, ' ') << ResetColor() << std::endl;
255         }
256     }
