258     // Print a table with headers and rows.
259     // Each column's width is determined by the widest element (header or cell) in that column.
260     static inline void PrintTable(const std::string& title, const std::vector<std::string>& header, const std::vector<std::vector<std::string>>& rows, Color color = Color::DEFAULT)
261     {
262         size_t cols = header.size();
263         std::vector<size_t> colWidths(cols, 0);
264 
265         // Determine max width for each column from the header.
266         for (size_t i = 0; i < cols; i++)
267         {
268             colWidths[i] = DisplayLength(header[i]);
269         }
270 
271         // Update max width from the rows.
272         for (const auto &row : rows)
273         {
274             for (size_t i = 0; i < row.size() && i < cols; i++)
275             {
276                 size_t cellLength = DisplayLength(row[i]);
277                 if (cellLength > colWidths[i])
278                     colWidths[i] = cellLength;
279             }
280         }
281 
282         // Helper lambda to repeat a string.
283         auto repeat = [](const std::string &s, size_t count) {
284             std::string result;
285             for (size_t i = 0; i < count; ++i)
286                 result += s;
287             return result;
288         };
289 
290         std::ostringstream oss;
291 
292         // Top border.
293         oss << Cta(color) << "‚îå";
294         for (size_t i = 0; i < cols; i++)
295         {
296             if (i == 0)
297                 oss << title << repeat("‚îÄ", colWidths[i] + 2 - DisplayLength(title));
298             else
299                 oss << repeat("‚îÄ", colWidths[i] + 2);
300             oss << (i < cols - 1 ? "‚î¨" : "‚îê");
301         }
302         oss << ResetColor() << std::endl;
303         std::cout << oss.str();
304 
305         // Header row.
306         oss.str("");
307         oss.clear();
308         oss << Cta(color) << "‚îÇ" << ResetColor();
309         for (size_t i = 0; i < cols; i++)
310         {
311             oss << " " << Cta(Color::YELLOW) << BoldText(header[i]);
312             size_t pad = colWidths[i] > DisplayLength(header[i]) ? colWidths[i] - DisplayLength(header[i]) : 0;
313             oss << std::string(pad, ' ') << " " << Cta(color) << "‚îÇ" << ResetColor();
314         }
315         oss << std::endl;
316         std::cout << oss.str();
317 
318         // Header separator.
319         oss.str("");
320         oss.clear();
321         oss << Cta(color) << "‚îú";
322         for (size_t i = 0; i < cols; i++)
323         {
324             oss << repeat("‚îÄ", colWidths[i] + 2);
325             oss << (i < cols - 1 ? "‚îº" : "‚î§");
326         }
327         oss << ResetColor() << std::endl;
328         std::cout << oss.str();
329 
330         // Data rows.
331         for (const auto &row : rows)
332         {
333             oss.str("");
334             oss.clear();
335             oss << Cta(color) << "‚îÇ" << ResetColor();
336             for (size_t i = 0; i < cols; i++)
337             {
338                 // Use an empty string if this row doesn't have enough columns.
339                 std::string cell = (i < row.size()) ? row[i] : "";
340                 oss << " " << cell;
341                 size_t pad = colWidths[i] > DisplayLength(cell) ? colWidths[i] - DisplayLength(cell) : 0;
342                 oss << std::string(pad, ' ') << " " << Cta(color) << "‚îÇ" << ResetColor();
343             }
344             oss << std::endl;
345             std::cout << oss.str();
346         }
347 
348         // Bottom border.
349         oss.str("");
350         oss.clear();
351         oss << Cta(color) << "‚îî";
352         for (size_t i = 0; i < cols; i++)
353         {
354             oss << repeat("‚îÄ", colWidths[i] + 2);
355             oss << (i < cols - 1 ? "‚î¥" : "‚îò");
356         }
357         oss << ResetColor() << std::endl;
358         std::cout << oss.str();
359     }
